// LandingPage.jsx
import { motion } from "framer-motion"
import nasaLogo from "./nasa-logo.png" // mets ton logo NASA
import pyroLogo from "./logo.png" // mets ton logo PyroSat si tâ€™en as

export default function LandingPage() {
  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-br from-black via-gray-900 to-red-900 text-white">

      {/* Logo + titre */}
      <motion.div
        initial={{ opacity: 0, y: -40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="flex flex-col items-center mb-10"
      >
        <img src={pyroLogo} alt="PyroSat Logo" className="w-20 mb-4" />
        <h1 className="text-4xl font-bold">Welcome to <span className="text-red-500">PyroSat</span></h1>
        <p className="text-lg text-gray-300 mt-2">
          Worldwide Wildfire Tracking <br /> Powered by <img src={nasaLogo} alt="NASA" className="inline w-12 ml-2" />
        </p>
      </motion.div>

      {/* Bouton */}
      <motion.a
        href="/app" // la route vers ta plateforme
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.6, delay: 0.3 }}
        className="px-8 py-3 bg-red-600 hover:bg-red-700 text-lg font-semibold rounded-full shadow-lg transition-all"
      >
        ðŸš€ Launch Platform
      </motion.a>
    </div>
  )
}
