import { useState } from 'react'
import GoogleMapReact from 'google-map-react'
import LocationMarker from './LocationMarker'
import LocationInfoBox from './LocationInfoBox'

// define constants
const NATURAL_EVENT_WILDFIRE = 8;

const Map = ({ eventData, center, zoom }) => {
    const [locationInfo, setLocationInfo] = useState(null)

    const markers = eventData
  .filter(ev => ev.categories[0].id === NATURAL_EVENT_WILDFIRE) // wildfires
  .filter(ev => {
    const lastGeometry = ev.geometries[ev.geometries.length - 1];
    const eventDate = new Date(lastGeometry.date);
    const now = new Date();
    const hoursDiff = (now - eventDate) / (1000 * 60 * 60); // différence en heures
    return hoursDiff <= 48; // considère "LIVE" si < 48h
  })
  .map((ev, index) => (
    <LocationMarker
      key={index}
      lat={ev.geometries[0].coordinates[1]}
      lng={ev.geometries[0].coordinates[0]}
      onClick={() => setLocationInfo({ id: ev.id, title: ev.title })}
    />
  ));


    return (
        <div className="map">
            <GoogleMapReact
                bootstrapURLKeys={{ key: 'AIzaSyB9GNxKwOKC_EEij_cTqbizoV6wjlSyYow' }}
                defaultCenter={ center }
                defaultZoom={ zoom }
            >
                {markers}
            </GoogleMapReact>
            {locationInfo && <LocationInfoBox info={locationInfo} />}
        </div>
    )
}

Map.defaultProps = {
    center: {
        lat: 42.3265,
        lng: -122.8756
    },
    zoom: 6
}

export default Map
